mapper.select:
SELECT df.doc_form_idx, dt.doc_type_name, df.doc_form_name, e.emp_name, df.doc_form_use, df.doc_form_upHit FROM employee e, document_type dt, doc_form df
df.emp_id = e.emp_id AND dt.doc_type_idx = df.doc_type_idx df.emp_id = e.emp_id AND dt.doc_type_idx = df.doc_type_idx AND dt.doc_type_idx = #{param1}
ORDER BY doc_form_idx desc doc_form_use desc doc_form_upHit desc

SELECT dt.doc_type_name, df.doc_form_idx, df.doc_form_name, e.emp_name, t.team_name, df.doc_form_content, df.doc_form_use, df.doc_form_upHit
	FROM employee e, document_type dt, doc_form df, team t 
	WHERE df.emp_id = e.emp_id AND dt.doc_type_idx = df.doc_type_idx AND t.team_idx = e.team_idx AND doc_form_idx=#{param1};
